// import React, { useState } from "react";
import React, { useEffect, useState } from "react";
import Button from "../components/Button";
import "../css/login.css";
import { Link } from "react-router-dom";

function Login() {

    const [color, setColor] = React.useState('#ECE9E9');

    React.useEffect(() => {
        document.body.style.backgroundColor = color;
    }, [color]);


    const [emailInput, setEmailInput] = useState("");
    const [senhaInput, setSenhaInput] = useState("");

    function entrar(e) {
        e.preventDefault();
        console.log(emailInput);
        console.log(senhaInput);
    }

    return (
        <>
            <div className="box">
                <div className="text-container">
                   <h3>Bem-Vindo</h3>
                </div>

                <div className="input-container">

                    <form onSubmit={Login} >
                        <label className="label-form">Email</label><br />
                        <input className="input-form" type="email" value={emailInput} onChange={e => setEmailInput(e.target.value)} />
                        <br />
                        <br />
                        <label className="label-form">Senha</label><br />
                        <input className="input-form" type="password" value={senhaInput} onChange={e => setSenhaInput(e.target.value)} />
                        <br />
                        <br />

                        <Button title="Entrar" />
                    </form>
                </div>

                <div className="link">
                    {/* href faz aparecer aviso no terminal e polui o console, removi temporariamente até fazermos as rotas */}
                    <h4>Não possuí uma conta?
                        <Link to="/cadastro">
                            <a className="login"> Cadastre-se</a>
                        </Link>
                    </h4>

                </div>
            </div>
        </>
    );
}

export default Login;
